export default class Gage extends Component{

  constructor() {
    super();

    //this.app = firebase.initializeApp(FB_CONFIG);
    this.firebaseDatabase = firebaseDatabase;
    this.database = this.firebaseDatabase.ref("object").child("SPEED");

    this.state = {
      speed: 30,

    };
  }
  componentDidMount() {
    this.database.on("value", snap => {
      this.setState({
        speed: snap.val()
      });
    });
  }


  render(){
  <div>
  <h1> In Car Test With Firebase Data </h1>
  <h1>{this.state.speed}</h1>
  <h1>{this.state.rpm}</h1>

  <h1>Gauge</h1>
  <Gauge
    value={this.state.speed.toString()}
    min="0"
    max={parseInt("100")}
    label="Speed"
  />

   </div>
  }
  }
