import React, { Component } from 'react';
import { firebaseDatabase } from "./firebase";
import Trend from 'react-trend';

export default class LineGraph extends Component{

    constructor(props) {
        super(props);
        this.firebaseDatabase = firebaseDatabase;
        this.database1 = this.firebaseDatabase.ref('devID02').child('data').child('HISTORY').child('1');

        this.state= {
            y1:0
        };
    }

    componentDidMount() {
        this.database1.on("value", snap => {
            this.setState({
            y1: snap.val()
            });
        });
    }

render(){
    return(
    <div>
        <Trend
    smooth
    autoDraw
    autoDrawDuration={3000}
    autoDrawEasing="ease-out"
    data={[0,2,5,9,5,10,3,Math.abs(this.state.y1),5,0,0,1,8,2,9,0]}
    gradient={['#00c6ff', '#F0F', '#FF0']}
    radius={10}
    strokeWidth={5}
    strokeLinecap={'butt'}
     />
    </div>
)
}
}
