import React, { Component } from 'react';
import { firebaseDatabase } from "./firebase";
import Trend from 'react-trend';
import './LineGraph.css';


export default class LineGraph extends Component{

    constructor(props) {
        super(props);
        var keyVal = this.props.keyVal
        var devId = this.props.devId
        var name= this.props.nameVal
        this.firebaseDatabase = firebaseDatabase;
        this.database1 = this.firebaseDatabase.ref(devId).child('data').child(keyVal);

        this.state= {
            dValue:0,
            //q: new Array(5),
            q: [0,0,0,0,0]
        };
    }

    componentDidMount() {
        this.database1.on("value", snap => {
            this.setState({
            dValue: snap.val()
            });
        });
    }

    lineGraphData(){
    var numUpdate = parseInt(this.state.dValue)

    if (this.state.q.length <= 5 ){
        this.state.q.push(numUpdate);
    }
    else {
        this.state.q.push(numUpdate);
        this.state.q.shift();
    }
};    


render(){
    this.lineGraphData();
    console.log('Data Array: '+ this.state.q)

    console.log(this.state.dValue)
    return(
        <div>
        <Trend width={300} height={200}
            smooth
            autoDraw
            autoDrawDuration={5}
            autoDrawEasing="ease-out"
            data={this.state.q}
            //data={[4,3,2]}
            gradient={['white']}
            radius={25}
            strokeWidth={4}
            strokeLinecap={'butt'}
            />
        <h1>{this.state.name}</h1>     
    </div>
)
}
}
