import { firebaseDatabase } from "./firebase";
import Trend from 'react-trend';

export default class LineGraph extends Component{

    constructor(props) {
        super(props);
        this.firebaseDatabase = firebaseDatabase;
        this.database1 = this.firebaseDatabase.ref('devID02').child('data').child('HISTORY').child('1');

        this.state= {
            y1:0,
            q:[0,0]

        };
    }

    componentDidMount() {
        this.database1.on("value", snap => {
            this.setState({
            y1: snap.val()
            });
        });
    }

    lineGraphData(){
    var numUpdate = this.state.y1



    if (this.state.q.length <= 10 ){
        this.state.q.push(numUpdate);
    }
    else {
        this.state.q.push(numUpdate);
        this.state.q.shift();
    }
};    


render(){
    this.lineGraphData();
    console.log('hello '+ this.state.q);
    return(

    <div>
        <Trend

    smooth
    autoDraw
    autoDrawDuration={300}
    autoDrawEasing="ease-in"
    data={this.state.q}
    gradient={['#00c6ff', '#F0F', '#FF0']}
    radius={10}
    strokeWidth={5}
    strokeLinecap={'butt'}
     />
    </div>
)
}
}
